mapper.insert:
  	
	  	INSERT INTO job_info(
	  		job_name, retry_count, time_expression, submit_ip,
	  		execute_url, execute_date, is_valid
	  	)VALUES(
	  		#{jobName}, #{retryCount}, #{timeExpression}, #{submitIp}, #{executeUrl},
	  		#{executeDate},
	  		#{isValid}
	  	)
  	
        
            SELECT LAST_INSERT_ID() as id

mapper.update:
UPDATE job_info
SET execute_date=#{executeDate}
WHERE id = #{id}

UPDATE job_info
SET is_valid = #{isValid}
WHERE id = #{id}

mapper.select:
SELECT

FROM job_info
WHERE execute_date<=#{untilDate}
AND is_valid=1
LIMIT #{limit}

SELECT execute_date
FROM job_info
WHERE execute_date>#{startDate}
AND execute_date<=#{startDate}
AND is_valid=1
ORDER BY execute_date
LIMIT #{limit}
