mapper.update:
UPDATE job_info
SET execute_date=#{executeDate}, retry_count=#{retryCount}
WHERE id = #{id}

UPDATE job_info
SET is_valid = #{isValid}
WHERE id = #{id}

UPDATE job_info
SET job_owner = #{jobOwner}, department = #{department}
WHERE id = #{id}

UPDATE job_info
SET job_owner = #{jobOwner}
WHERE id = #{id}

UPDATE job_info
SET department = #{department}
WHERE id = #{id}

mapper.select:
SELECT

FROM job_info
WHERE execute_date<=#{untilDate}
AND is_valid=1
LIMIT #{limit}

SELECT

FROM job_info
WHERE 1 = 1

    AND business_name = #{businessName}


    AND name = #{name}


    AND job_owner = #{jobOwner}


    AND department = #{department}

ORDER BY id DESC
LIMIT #{pageSize}

SELECT

FROM job_info
WHERE business_name = #{businessName}
AND name = #{name}
LIMIT 1

mapper.delete:
DELETE FROM job_info
WHERE business_name = #{businessName}
AND name = #{name}
